mapper.select:
select cartno , cartstock, cartregdate, mid, pcode from cart where mid=#{mid}
order by cartregdate desc
order by cartregdate

mapper.update:
update 	product
set pstock=#{pstock}, psel=#{psel}
where pcode=#{pcode}

mapper.select:
select count(*)
from cart 
where pcode=#{pcode} and mid=#{mid}
